import React, { useEffect, useState, useRef } from "react";
import { Link } from "react-router-dom";
import {
  Dropdown,
  DropdownToggle,
  DropdownMenu,
  DropdownItem,
  Collapse,
  NavItem,
  NavLink
} from "shards-react";
import axios from 'axios';

export default class UserActions extends React.Component {

  // const [user_name, setUser_name] = useState("");

  state ={
    request_receive_num: 0
  }

  constructor(props) {
    super(props);

    this.state = {
      visible: false
    };

    this.toggleUserActions = this.toggleUserActions.bind(this);
    
    
  }
  
  // async componentDidMount() {
  // }
  

  toggleUserActions() {
    this.setState({
      visible: !this.state.visible
    });


    axios.get('http://localhost:3001/api/check_request_receive_num')
    .then(response => {
      const posts = response.data;
      this.setState ({request_receive_num: posts});
    })


  }

  log_out(){
    sessionStorage.removeItem('user_id')
    document.location.href = '/'
  }
  // useEffect(() => {
  
  //   async function user_set_id(){

  //     setUser_name(sessionStorage.getItem('user_id'));
  //   }
  //   user_set_id();
  
  // }, []);


//   render() {
//     return (
//       <NavItem tag={Dropdown} caret toggle={this.toggleUserActions}>
//         <DropdownToggle caret tag={NavLink} className="text-nowrap px-3">
//           <span className="d-none d-md-inline-block">{sessionStorage.getItem('user_id')}</span>
//         </DropdownToggle>
//         <Collapse tag={DropdownMenu} right small open={this.state.visible}>
//           <DropdownItem onClick={this.log_out} className="text-danger">
//             <i className="material-icons text-danger">&#xE879;</i> Logout
//           </DropdownItem>
//           <DropdownItem onClick={this.log_out}>
//             <i className="material-icons">&#xE7FD;</i> 트랜잭션(관리자)
//           </DropdownItem>
//         </Collapse>
//       </NavItem>
//     );
//   }
// }




  render() {
    if (sessionStorage.getItem('user_id') == 'admin'){
      return (<NavItem tag={Dropdown} caret toggle={this.toggleUserActions}>
                <DropdownToggle caret tag={NavLink} className="text-nowrap px-3">
                  <span className="d-none d-md-inline-block">{sessionStorage.getItem('user_id')}</span>
                </DropdownToggle>
                <Collapse tag={DropdownMenu} right small open={this.state.visible}>
                  <DropdownItem onClick={this.log_out} className="text-danger">
                    <i className="material-icons text-danger">&#xE879;</i> Logout
                  </DropdownItem>
                  <DropdownItem tag={Link} to="/admin">
                    <i className="material-icons">&#xE7FD;</i> 트랜잭션(관리자)
                  </DropdownItem>
                </Collapse>
              </NavItem>
              )
    }
    else if (sessionStorage.getItem('user_id') == 'aaa'){
      return (<NavItem tag={Dropdown} caret toggle={this.toggleUserActions}>
                <DropdownToggle caret tag={NavLink} className="text-nowrap px-3">
                  <span className="d-none d-md-inline-block">{sessionStorage.getItem('user_id')}</span>
                </DropdownToggle>
                <Collapse tag={DropdownMenu} right small open={this.state.visible}>
                  <DropdownItem onClick={this.log_out} className="text-danger">
                    <i className="material-icons text-danger">&#xE879;</i> Logout
                  </DropdownItem>
                  <DropdownItem tag={Link} to="/rejectedTransaction">
                    <i className="material-icons text-danger">notifications</i> 알람
                    {this.state.request_receive_num}

                  </DropdownItem>
                </Collapse>
              </NavItem>
              )
    }
    else if (sessionStorage.getItem('user_id') == 'bbb'){
      return (<NavItem tag={Dropdown} caret toggle={this.toggleUserActions}>
                <DropdownToggle caret tag={NavLink} className="text-nowrap px-3">
                  <span className="d-none d-md-inline-block">{sessionStorage.getItem('user_id')}</span>
                </DropdownToggle>
                <Collapse tag={DropdownMenu} right small open={this.state.visible}>
                  <DropdownItem onClick={this.log_out} className="text-danger">
                    <i className="material-icons text-danger">&#xE879;</i> Logout
                  </DropdownItem>
                  <DropdownItem tag={Link} to="/grantTransaction">
                    <i className="material-icons text-danger">notifications</i> 알람
                    {this.state.request_receive_num}
                  </DropdownItem>
                </Collapse>
              </NavItem>
              )
    }
    else {
      return (<NavItem tag={Dropdown} caret toggle={this.toggleUserActions}>
                  <DropdownToggle caret tag={NavLink} className="text-nowrap px-3">
                    <span className="d-none d-md-inline-block">{sessionStorage.getItem('user_id')}</span>
                  </DropdownToggle>
                  <Collapse tag={DropdownMenu} right small open={this.state.visible}>
                    <DropdownItem onClick={this.log_out} className="text-danger">
                      <i className="material-icons text-danger">&#xE879;</i> Logout
                    </DropdownItem>
                  </Collapse>
                </NavItem>
      )
    }
  }
}
